function getAlarmRecords(error, res) {
    if (error) {
        console.log("error");
    } else {
        alarmArray = JSON.parse(res.response);

        if (alarmArray.length != 0) {

            filteredArray = [];

            for (var i in alarmArray) {

                link = "/rte.html?project=" + projectId + "&amp;page=" + PageId + "&amp;seniorID=" + alarmArray[i]["Impact"];

                getLink = "&lt;a href='" + link + "' target='_blank'&gt;" + alarmArray[i]["Impact"] + "&lt;/a&gt;";
                filteredArray[i] = {};
                filteredArray[i]["Senior ID"] = getLink;
                filteredArray[i]["Site ID"] = alarmArray[i]["siteName"];
                filteredArray[i]["Time"] = alarmArray[i]["TimeStamp"];
            }
            SetPluginParameterValue("GridPlugin 1", "Query", filteredArray);
            DrawPlugin("GridPlugin 1");
            ShowPlugin("GridPlugin 1");
        }
    }
}

